import { Component, OnInit } from '@angular/core';
import { ProductsService } from '../products.service';
import { TriProduitsPipe } from '../tri-produits.pipe';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  produits: any[] = [];
  searchText = '';
  filteredProduits: any[] = [];
  responseText: string = ''; // Modifiez le type de la variable
  selectedSortOption: string = "prix-decroissant";

  constructor(private productsService: ProductsService) {}

  filterProduits() {
    this.filteredProduits = this.produits.filter(produit => {
      const isMatching = produit.product_name.toLowerCase().includes(this.searchText.toLowerCase());
      if(isMatching) {
        this.responseText = '';
        return true;
      }
      else {
        this.responseText = 'Aucun produit correspondant';
        return false;
      }
    });
  }

  ngOnInit() {
    this.produits = this.productsService.dataProducts;
    this.filterProduits();
  }

  onSortOptionChanged() {

  }
}
<section class="section-home">


  <article class="article-home">

    <h1>Notre entreprise</h1>
    <div class="courte-pres-home">
      <img class="image-presentation" src="./assets/images/figurines.jpg" alt="figurines astérix et obélix">

      <p class="para-presentation">
        Bienvenue "Au Petit Village" ! Nous sommes une entreprise spécialisée dans la vente de figurines inspirées d'Astérix et Obélix, toutes fabriquées à la main.<br><br>

        "Au Petit Village", nous nous sommes plongés dans l'univers captivant d'Astérix et Obélix pour créer des figurines uniques qui célèbrent l'esprit et l'aventure de ces héros gaulois bien-aimés.<br><br>

        Chaque figurine est méticuleusement conçue et sculptée à la main par nos artisans passionnés. <br><br>

        "Au Petit Village" - L'endroit où les légendes d'Astérix et Obélix prennent vie !
      </p>
    </div>


  </article>
  <article class="article-home">

    <div class="firstdiv-article-home">

      <h2>Nos produits</h2>
      <input [(ngModel)]="searchText" (input)="filterProduits()" placeholder="Rechercher un produit">

      <select [(ngModel)]='selectedSortOption' >
        <option selected value="prix-croissant">Prix croissant</option>
        <option value="prix-decroissant">Prix décroissant</option>
      </select>
      <p *ngIf="responseText === 'Aucun produit correspondant' && filteredProduits.length === 0">{{responseText}}</p>
    </div>


    <div class="container-produit">
      <div class="produit" *ngFor="let produit of filteredProduits | triProduits:selectedSortOption">

        <a class="lien-produit" [routerLink]="['/product', produit.id]">
          <img src={{produit.product_img}} alt="Figurine de {{produit.product_name}}">
          <h3>{{ produit.product_name }}</h3>
          <p>{{produit.product_desc}}</p>
          <p>Prix: <strong>{{produit.product_price}}€</strong></p>
        </a>

      </div>
    </div>
  </article>

</section>
