import {Component, OnInit} from '@angular/core';
import {ProductsService} from "../products.service";
import {ActivatedRoute} from "@angular/router";

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.scss']
})
export class ProductDetailComponent implements OnInit{
  product: any;

  constructor(private route: ActivatedRoute, public productService: ProductsService) {
  }

  ngOnInit() {
    const productId = Number(this.route.snapshot.paramMap.get('id'));
    this.product = this.productService.dataProducts.find(product => product.id === productId)

  }
}
<section>

  <article>
    <img src={{product.product_img}} alt={{product.product_name}}>
    <h2>Figurine {{product.product_name}}</h2>
    <p>Prix : <strong>{{product.product_price}}€</strong></p>
    <p>Nombre de figurines restantes : {{product.stock}}</p>
    <p><strong>Description:</strong><br> {{product.product_details}}</p>
    <div>
      <p><strong>Caractéristiques techniques: </strong><br></p>
      <ul>
        <li><span>Taille du produit:</span> {{product.taille}}</li>
        <li><span>Matière:</span> {{product.materiaux}}</li>
      </ul>
    </div>
    <button class="bouton">Acheter</button>

  </article>

</section>
